(()=>{"use strict";new class{constructor(){this.submitHandler=t=>{t.preventDefault();const e=document.querySelector("input[name=rating]:checked");if(!e)return void alert("You have to select a value");this.ratings.reset();const o=e.getAttribute("value");this.showModal(o)},this.showModal=t=>{this.modal=document.querySelector(".modal-rating"),this.modal.setAttribute("opened",""),document.querySelector(".selected-rate-text").textContent=`You selected ${t} out of 5`},this.hideModal=()=>{this.modal.removeAttribute("opened")},this.ratings=document.querySelector(".rating-values"),this.ratings.addEventListener("submit",this.submitHandler),document.querySelector(".modal-rating").addEventListener("click",this.hideModal)}}})();